import styled from 'styled-components';

type ButtonProps = {
  primary: boolean;
};

export const Container = styled.div`
  width: 350px;
  height: 475px;
  background: var(--bg-primary);
`;

export const Header = styled.div`
  width: 350px;
  height: 125px;
  background: var(--bg-secundary);
  padding: 0px 20px;

  display: flex;
  align-items: center;
  justify-content: space-between;
`;

export const Title = styled.h1`
  background: white;
`;

export const Formula = styled.span`
  font-size: 1.8rem;
  color: var(--text-secundary);
`;

export const NumPad = styled.div`
  margin-top: 20px;
  display: grid;
  grid-template-columns: 87.5px 87.5px 87.5px 87.5px;
`;

export const Key = styled.button<ButtonProps>`
  display: flex;
  justify-content: center;
  align-items: center;
  background: ${(props) =>
    props.primary ? 'var(--bg-secundary)' : 'var(--bg-primary)'};
  color: ${(props) =>
    props.primary ? 'var(--text-secundary)' : 'var(--text-primary)'};
  font-size: 1.5rem;
  border-radius: ${(props) => (props.primary ? '50%' : '0%')};

  &:active,
  &:hover {
    color: #fff;
  }
`;
