import styled from 'styled-components';

interface KeyProps {
  math?: boolean;
}

export const Key = styled.button<KeyProps>`
  display: flex;
  justify-content: center;
  align-items: center;
  background: ${(props) =>
    props.math ? 'var(--bg-primary)' : 'var(--bg-secundary'};
`;
