import styled from "styled-components";

export const Container = styled.div`
  width: 150px;
  height: 150px;
  background: #8757e5;
  border-radius: 50%;
  color: #ffffff;
  font-size: 0.7rem;

  display: flex;
  justify-content: center;
  align-items: center;

  span {
    &:hover {
      border-bottom: 5px solid #ffffff;
    }
  }
`;
