import React, { useState } from 'react';
import Button from './Button';
import { Container, Formula, Header, NumPad, Title } from './style';

const Calculator: React.FC = () => {
  const [display, setDisplay] = useState<string>('');
  const [formula, setFormula] = useState<string[]>(['']);

  const handleClick = (n: string) => {

    const length = formula.length;
    console.log(formula);

    switch(n) {

    case 'C':
      setDisplay('');
      setFormula([]);
      break;

    case '×':
      setFormula(formula => [...formula].concat(''));
      break;

    case '÷':
      break;

    case '+':
      break;

    case '=':
      break;

    case '.':
      break;

    default:
      setDisplay(display + n);

      if (formula.length !== 0) {
        setFormula([...formula].concat(n));
      }
      break;

    }
  };

  return (
    <Container>
      <Header>
        <Title></Title>
        <Formula>{display}</Formula>
      </Header>
      <NumPad>
        <Button label={'C'} onClick={handleClick} math/>
        <Button label={'×'} onClick={handleClick} math/>
        <Button label={'+'} onClick={handleClick} math/>
        <Button label={'÷'} onClick={handleClick} math/>
        <Button label={'7'} onClick={handleClick}/>
        <Button label={'8'} onClick={handleClick}/>
        <Button label={'9'} onClick={handleClick}/>
        <Button label={'−'} onClick={handleClick} math/>
        <Button label={'4'} onClick={handleClick}/>
        <Button label={'5'} onClick={handleClick}/>
        <Button label={'6'} onClick={handleClick}/>
        <Button label={'='} onClick={handleClick} math height={3}/>
        <Button label={'1'} onClick={handleClick}/>
        <Button label={'2'} onClick={handleClick}/>
        <Button label={'3'} onClick={handleClick}/>
        <Button label={'0'} onClick={handleClick}/>
        <Button label={'.'} onClick={handleClick} width={2}/>

      </NumPad>
    </Container>
  );
};

export default Calculator;
