import styled from 'styled-components';

interface KeyProps {
  math?: boolean;
  width?: number;
  height?: number;
}

export const Key = styled.button<KeyProps>`
  background: ${(props) =>
    props.math ? 'var(--bg-secundary)' : 'var(--bg-primary)'};
  color: ${(props) =>
    props.math ? 'var(--text-secundary)' : 'var(--text-primary)'};
  border-radius: 10px;
  margin: 10px;
  padding: 10px;
  font-size: 1.25rem;
  grid-column: ${(props) => (props.height ? `span ${props.height}` : 'initial')}
  grid-row: ${(props) => (props.height ? `span ${props.height}` : 'initial')};
`;
