import { Container, Formula, Header, NumPad, Key, Title } from './style';

const Calculator: React.FC = () => {
  const [formula, setFormula] = useState('');  

  const handleClick = () => {
    console.log(event?.target);
  };

  return (
    <Container>
      <Header>
        <Title></Title>
