import React, { useState } from 'react';
import { Container } from './style';

type user = {
  name: string
}

const User = (user: user) => {
	const [counter, setCounter] = useState(0);
	const colors = ['lightcoral', 'lightblue', 'orange'];
	const emojis = ['ðŸ¤£', 'ðŸ˜°', 'ðŸŒŒ'];

	const handleClick = () => {
		const randomNumber = Math.floor(Math.random() * colors.length);
		console.log(randomNumber);

		if (counter >= 0 && counter < colors.length - 1)
			setCounter(counter + 1);
		else
			setCounter(0);
	};

	return (
		<Container>
			<span style={{background: `${colors[counter]}`}}>{user.name} {emojis[counter]}</span>
			<button onClick={handleClick}>Contar +1</button>
		</Container>
	);
};

export default User;
