import React, { useState } from 'react';
import Button from './Button';
import { Container, Formula, Header, NumPad, Title } from './style';

const Calculator: React.FC = () => {
  const [display, setDisplay] = useState<string | number>('');

  const handleClick = (n: number) => {
    setDisplay(n);
  };

  return (
    <Container>
      <Header>
        <Title></Title>
        <Formula>{display}</Formula>
      </Header>
      <NumPad>
        <Button label={'1'} onClick={handleClick(1)}/>
        <Button label={'2'} onClick={handleClick(1)}/>
        <Button label={'3'} onClick={handleClick(1)}/>
        <Button label={'4'} onClick={handleClick(1)}/>
        <Button label={'5'} onClick={handleClick(1)}/>
        <Button label={'6'} onClick={handleClick(1)}/>
        <Button label={'7'} onClick={handleClick(1)}/>
        <Button label={'8'} onClick={handleClick(1)}/>
        <Button label={'9'} onClick={handleClick(1)}/>
        <Button label={'0'} onClick={handleClick(1)}/>
      </NumPad>
    </Container>
  );
};

export default Calculator;
