import styled from "styled-components";

export const Container = styled.div`
  width: 150px;
  height: 150px;
  background: #8757e5;
  border-radius: 50%;
  color: #ffffff;
  font-size: 0.7rem;

  display: flex;
  justify-content: center;
  align-items: center;

  span {
    position: relative;

    &:hover {
      transition: transform 1s all;
    }

    &:hover::before {
      content: "";
      position: absolute;
      width: 100%;
      height: 5px;
      bottom: -5px;
      background: #ffffff;
    }
  }
`;
