import styled from 'styled-components';

interface KeyProps {
  math?: boolean;
}

export const Key = styled.button<KeyProps>`
  background: ${(props) =>
    props.math ? 'var(--bg-secundary)' : 'var(--bg-primary)'};
  color: ${(props) =>
    props.math ? 'var(--text-secundary)' : 'var(--text-primary)'};
  border-radius: 50%;
  margin: 15px;
  padding: 15px;
  font-size: 1.5rem;
`;
