import styled from 'styled-components';

export const Container = styled.div`
  width: 150px;
  height: 150px;
  background: #8757e5;
  border-radius: 50%;
  color: #ffffff;
  font-size: 0.8rem;

  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;

  span {
    position: relative;

    &::before {
      content: '';
      position: absolute;
      width: 100%;
      height: 3px;
      bottom: -3px;
      background: #ffffff;
      transform: scaleX(0);
      transition: transform 1s;
    }

    &:hover::before {
      transform: scaleX(1);
    }
  }

  button {
    padding: 4px 8px;
    background: #333;
    border-radius: 4px;
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    width: 50px;
    height: 50px;
    background: #3d0;
    border-radius: 50%;
  }

  &::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 50px;
    height: 50px;
    background: #d30;
    border-radius: 50%;
  }
`;
